import Vue from 'vue'
import Router from 'vue-router'
import AddTask from 'C:\Users\joaqu\Desktop\BOOTCAMP\Practice\BOOTCAMP-practice\p_semana_7\GestorTareas-Plantilla\src\views\AddTask.vue'
import TaskList from '../components/TaskList.vue'
import CombinedView from '../components/CombinedView.vue'

Vue.use(Router)

export default new Router({
  mode: 'history',
  routes: [
    {
      path: '/',
      name: 'CombinedView',
      component: CombinedView
    },
    {
      path: '/about',
      name: 'TaskList',
      component: TaskList
    },
    {
      path: '/addtask',
      name: 'AddTask',
      component: AddTask
    }
  ]
})
